class PostModel {
  PostModel({
      String? msg, 
      List<Post>? post,}){
    _msg = msg;
    _post = post;
}

  PostModel.fromJson(dynamic json) {
    _msg = json['msg'];
    if (json['post'] != null) {
      _post = [];
      json['post'].forEach((v) {
        _post?.add(Post.fromJson(v));
      });
    }
  }
  String? _msg;
  List<Post>? _post;
PostModel copyWith({  String? msg,
  List<Post>? post,
}) => PostModel(  msg: msg ?? _msg,
  post: post ?? _post,
);
  String? get msg => _msg;
  List<Post>? get post => _post;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['msg'] = _msg;
    if (_post != null) {
      map['post'] = _post?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

